import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CustomerService } from '../services/customer.service';
import { IModClient } from '../Interface/modClient';
import { ICreateClients } from '../Interface/createClient';
import { IConsultById } from '../Interface/consultById';

@Component({
  selector: 'app-mod-client',
  templateUrl: './mod-client.component.html',
  styleUrls: ['./mod-client.component.css']
})
export class ModClientComponent implements OnInit {
  @Input() IDClient! : string;
  upClient : FormGroup;
  dataModCustumer! : ICreateClients;
  submitted = false;
  errorserver = false;
  DocNum! : string;

  ArrTypeContac: any[] = [];
  ArrContac: any[] = [];
  ArrCheckContac: any[] = [];
  ArrPrefeContac: any[] = [];

  constructor(private ServiceCustomer : CustomerService, private fbModClient : FormBuilder) { 
    this.upClient = this.fbModClient.group({
      firstName: [''], 
      lastName: [''],
      surnames: ['', ],
      gender: [''],
      bank: [''],
      branch: [''],
      countryBir: [''],
      birthDate: [''],
      stateBir: [''],
      cityBir: [''],
      nationalities: [''],
      documentTypeDT: [''],
      countryDT: [''],
      cityDT: [''],
      stateDT: [''],
      expirationDate: [''],
      documentNumberDT: [''],
      maritalStatus: [''],
      contact: [''],
      contact2: [''],
      contact3: [''],
/*      economicActivity: [''],
       occupationType: [''],
      employmentSituation: [''],
      incomesType: [''],
      currency: [''],
      amount: [''], */
/*       nameWP: [''],
      documentTypeWP: [''],
      documentNumberWP: [''],
      workStartDateWP: [''],
      addressNameWP: [''],
      countryWP: [''],
      stateWP: [''],
      cityWP: [''], */
      addressNameD: [''],
      cityD: [''],
      stateD: [''],
      countryD: [''],
      personsInCharge: [''],
    });
  }

  ngOnInit(): void {
    this.getCustomersNew();
  }

  async getCustomersNew(){
    console.log('ask someone if getupClient work');
    console.log(this.IDClient);
    this.ServiceCustomer.getCustomerById(this.IDClient).subscribe(async (dataModCustumer) => {
      console.log('CONSULTA PERSONA');
      console.log(dataModCustumer);   

      let lasname;
      if (dataModCustumer['data']['surnames']) {
        lasname = dataModCustumer['data']['surnames'];
      } else {
        lasname = '';
      }

      let TipoDoc;
      let NumDoc = dataModCustumer['data']['identityDocuments'][0]['documentNumber'];
      if(dataModCustumer['data']['identityDocuments'][0]['documentType']['id'] == 'CC'){
        TipoDoc = "1"
      }else{
        TipoDoc = ""
      }

      this.DocNum = TipoDoc+NumDoc; 

      let IDmanager;
      if (dataModCustumer['data']['manager']['managerId']) {
        IDmanager = dataModCustumer['data']['manager']['managerId'];
      } else {
        IDmanager = '';
      }

      let PIDname ;
      if (dataModCustumer['data']['partnerInformation']['name']) {
        PIDname = dataModCustumer['data']['partnerInformation']['name'];
      } else {
        PIDname = '';
      }

      let PIDDTname ;
      if (dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name']) {
        PIDDTname = dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name'];
      } else {
        PIDDTname = '';
      }

      let PIDdocNumber ;
      if (dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber']) {
        PIDdocNumber = dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber'];
      } else {
        PIDdocNumber = '';
      }


      let Tam = dataModCustumer['data']['contactDetails'].length;
      let cont = 0;
      while (cont <= Tam-1) {
        let tc = dataModCustumer['data']['contactDetails'][cont]['contactType']['name'];
        let tempTc = this.ArrTypeContac.push(tc);
        let nc = dataModCustumer['data']['contactDetails'][cont]['contact'];
        let tempNc = this.ArrContac.push(nc);
        let pc = dataModCustumer['data']['contactDetails'][cont]['isPreferential'];
        let tempPc = this.ArrPrefeContac.push(pc);
        let vc = dataModCustumer['data']['contactDetails'][cont]['isChecked'];
        let tempVc = this.ArrCheckContac.push(vc);
        cont++;
      }

      this.upClient.setValue({
        firstName: dataModCustumer['data']['firstName'],
        lastName: dataModCustumer['data']['lastName'],
        surnames: lasname,
        birthDate: dataModCustumer['data']['birthDate'],
        cityBir: dataModCustumer['data']['identityDocuments'][0]['city'],
        countryBir:'',
        stateBir: '',
        nationalities: dataModCustumer['data']['nationalities'][0]['id'],
        documentTypeDT: dataModCustumer['data']['identityDocuments'][0]['documentType']['name'],
        documentNumberDT: dataModCustumer['data']['identityDocuments'][0]['documentNumber'],
        countryDT:'',
        stateDT:'',
        expirationDate:'',
        maritalStatus:'',
        contact: '',
        contact2: '',
        contact3: '',
        cityDT: dataModCustumer['data']['identityDocuments'][0]['city'],
        addressNameD: dataModCustumer['data']['legalAddress']['addressName'],
        cityD: dataModCustumer['data']['legalAddress']['city'],
        stateD: dataModCustumer['data']['legalAddress']['state'],
        countryD: dataModCustumer['data']['legalAddress']['country']['name'],
        membershipDate: dataModCustumer['data']['membershipDate'],
        gender: dataModCustumer['data']['gender']['id'],
        bank: '',
        branch: dataModCustumer['data']['bank']['branch']['name'],
        managerId: IDmanager,
        customerType: dataModCustumer['data']['customerType']['name'],
        education: dataModCustumer['data']['education']['level'],
        profession: dataModCustumer['data']['profession'],
        cardname: dataModCustumer['data']['cardName'],
        namePI: PIDname,
        documentTypePI: PIDDTname,
        documentNumberPI: PIDdocNumber,
        personsInCharge: dataModCustumer['data']['personsInCharge'],
      });    

    });
    console.log(this.DocNum);

    return this.DocNum;
  }

  updateClient(){
    console.log('ask someone if getupdateClient work');

    const dataClient : ICreateClients = {
      firstName: this.upClient.value.firstName, 
      lastName: this.upClient.value.lastName,
      surnames: this.upClient.value.surnames,
      gender: { id: this.upClient.value.gender },
      bank: {
        branch: { id: this.upClient.value.branch },
        id: this.upClient.value.bank,
      },
      birthData: {
        country: { id: this.upClient.value.countryBir },
        birthDate: this.upClient.value.birthDate,
        state: { id: this.upClient.value.stateBir },
        city: this.upClient.value.cityBir,
      },
      nationalities: [{ id: this.upClient.value.nationalities }],
      identityDocuments: [
        {
          documentType: { id: this.upClient.value.documentTypeDT },
          country: { id: this.upClient.value.countryDT },
          city: this.upClient.value.cityDT,
          state: { id: this.upClient.value.stateDT },
          expirationDate: this.upClient.value.expirationDate,
          documentNumber: this.upClient.value.documentNumberDT,
        },
      ],
      maritalStatus: { id: this.upClient.value.maritalStatus },
      contactDetails: [
        {
          contact: this.upClient.value.contact,
          contactType: { id: "MOBILE_NUMBER" },
        },
        {
          contact: this.upClient.value.contact2,
          contactType: { id: "EMAIL" },
        },
        {
          contact: this.upClient.value.contact3,
          contactType: { id: "OFFICE_NUMBER" },
        },
      ],
      economicData: {
        economicActivity: { id: this.upClient.value.economicActivity },
        occupationType: { id: this.upClient.value.occupationType },
        employmentSituation: this.upClient.value.employmentSituation,
        incomes: [
          {
            incomesType: { id: this.upClient.value.incomesType },
            incomesValue: {
              currency: this.upClient.value.currency,
              amount: this.upClient.value.amount,
            },
          },
        ],
        workPlace: {
          name: this.upClient.value.nameWP,
          taxpayerIdentification: {
            documentType: { id: this.upClient.value.documentTypeWP },
            documentNumber: this.upClient.value.documentNumberWP,
          },
          workStartDate: this.upClient.value.workStartDateWP,
          address: {
            location: {
              addressName: this.upClient.value.addressNameWP,
              country: { id: this.upClient.value.countryWP },
              state: { id: this.upClient.value.stateWP },
              city: this.upClient.value.cityWP,
            },
          },
        },
      },
      addresses: [
        {
          location: {
            addressName: this.upClient.value.addressNameD,
            city: this.upClient.value.cityD,
            state: { id: this.upClient.value.stateD },
            country: { id: this.upClient.value.countryD },
          },
        },
      ],
      personsInCharge: this.upClient.value.personsInCharge,
    };
    this.submitted = true;

/*     if(this.upClient.valid){
      this.stateValid = "Formulario Completo";
    }else{
      this.stateValid = "Formulario Incompleto";
      return;
    } */

    this.ServiceCustomer.updateCustomer(this.DocNum,dataClient).subscribe(data => {
      console.log(data);
    }, error => {
      this.errorserver = true;
      console.log("¡¡Cliente NO ACTUALIZADO!!");
    });

  }

  closeUpdate(){
    this.ServiceCustomer.$modalModify.emit(false); 
  }

}
<div id="global" class="modal">
    <div class="container-padding-modal">
        <body>
            <form [formGroup]="fgCustomer" (ngSubmit)="updateClient()">
                <div class="button-close">
                    <input type="image" (click)="closeUpdate()" src="https://cdn-icons-png.flaticon.com/512/458/458595.png">
                    <div class="spacer"></div>
                </div>
                <div class="user-details">
                    <div class="subtitle">
                        <h2>DATOS BÁSICOS</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Cliente: </span>
                        <input type="text" maxlength="16" formControlName="customerType">
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación Interna: </span>
                        <input type="text" maxlength="16" formControlName="clientId">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="16" formControlName="documentTypeDT">
                    </div>
                    <div class="input-box">
                        <span class="details">Número Identificación: </span>
                        <input type="text"  maxlength="15" formControlName="documentNumberDT">
                    </div>
                    <div class="input-box">
                        <span class="details">Nombres: </span>
                        <input type="text" maxlength="20" formControlName="firstName">
                    </div>
                    <div class="input-box">
                        <span class="details">Primer Apellido: </span>
                        <input type="text" maxlength="20" formControlName="lastName">
                    </div>
                    <div class="input-box">
                        <span class="details">Segundo Apellido: </span>
                        <input type="text" maxlength="20" formControlName="surnames">
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Género: </span>
                            <div class="category">
                                <label for="Id_genderF">F</label>
                                <input id="Id_genderF" type="radio" maxlength="1" value="F" formControlName="gender">
                                <label for="Id_genderM">M</label>
                                <input id="Id_genderM" type="radio" maxlength="1" value="M" formControlName="gender">
                            </div>
                    </div>  
                    <div class="input-box">
                        <span class="details">Fecha Nacimiento: </span>
                        <input type="text" maxlength="10" formControlName="birthDate">
                    </div>   
                    <div class="input-box">
                        <span class="details">Ciudad Expedición: </span>
                        <input type="text" maxlength="4" formControlName="cityBir">
                    </div>                                                      
                    <div class="input-box">
                        <span class="details">Nacionalidad: </span>
                        <input type="text" maxlength="4" formControlName="nationalities">
                    </div>  
                    <div class="input-box">
                        <span class="details">Personas a Cargo: </span>
                        <input type="number" maxlength="2" formControlName="personsInCharge">
                    </div>    
                    <div class="input-box">
                        <span class="details">Profesión: </span>
                        <input type="text" maxlength="2" formControlName="profession">
                    </div> 
                    <div class="input-box">
                        <span class="details">Nivel Educativo: </span>
                        <input type="text" maxlength="2" formControlName="education">
                    </div>   
                    <div class="subtitle">
                        <h2>INFORMACIÓN DEL BANCO</h2>
                    </div>                           
                    <div class="input-box">
                        <span class="details">Sucursal: </span>
                        <input type="text" maxlength="4" formControlName="branch">
                    </div>
                    <div class="input-box">
                        <span class="details">Membresía: </span>
                        <input type="text" maxlength="10" formControlName="membershipDate">
                    </div>
                    <div class="input-box">
                        <span class="details">Tarjeta: </span>
                        <input type="text" maxlength="26" formControlName="cardname">
                    </div> 
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE RESIDENCIA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">País Residencia: </span>
                        <input type="text" maxlength="3" formControlName="countryD">
                    </div>
                    <div class="input-box">
                        <span class="details">Departamento Residencia: </span>
                        <input type="text" maxlength="4" formControlName="stateD">
                    </div>  
                    <div class="input-box">
                        <span class="details">Ciudad Residencia: </span>
                        <input type="text" maxlength="20" formControlName="cityD">
                    </div> 
                    <div class="input-box-economic">
                        <span class="details">Dirección Residencia: </span>
                        <input type="text" maxlength="50" formControlName="addressNameD">
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE CONTACTO</h2>
                    </div>
                    <div class="contact-details">
                        <div class="input-box">
                            <span>Tipo</span>
                            <input type="text" *ngFor="let TypeC of ArrTypeContac; let i = index" id="contactType{{i+1}}" [ngModel]="TypeC" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" *ngFor="let TypeC of ArrTypeContac; let i = index" formControlName="contactType{{i+1}}" [ngModel]="TypeC" [disabled]="true">
                            <input type="text" maxlength="15" formControlName="contactType2">
                            <input type="text" maxlength="15" formControlName="contactType3"> -->
                        </div>
                        <div class="input-box">
                            <span>Número</span>
                            <input type="text" *ngFor="let NumC of ArrContac" [ngModel]="NumC" [ngModelOptions]="{standalone: true}" [disabled]="true">
<!--                             <input type="text" maxlength="80" formControlName="contact">
                            <input type="text" maxlength="80" formControlName="contact2">
                            <input type="text" maxlength="80" formControlName="contact3"> -->
                        </div>
                        <div class="input-box-contactCheck">
                            <span class="details">Preferencial: </span>
                            <input type="checkbox" *ngFor="let PrefC of ArrPrefeContac; let i = index" id="LCpreferencial{{i+1}}" [ngModel]="PrefC" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box-contactCheck">
                            <span class="details">Verificado: </span>
                            <input type="checkbox" *ngFor="let VerC of ArrCheckContac; let i = index" id="LCisChecked{{i+1}}" [ngModel]="VerC" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DEL SOCIO</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="20" formControlName="documentTypePI">
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación: </span>
                        <input type="text" maxlength="15" formControlName="documentNumberPI">
                    </div>
                    <div class="input-box">
                        <span class="details">Nombre: </span>
                        <input type="text" maxlength="20" formControlName="namePI">
                    </div>                  
                </div>
<!--                 <div class="button">
                    <input type="submit" value="Actualizar" (click)="UpdateClient()">
                </div> -->
            </form>
            <form [formGroup]="fgConsultFatca">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>CONSULTA FATCA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Id Fatca: </span>
                        <input type="text" maxlength="16" formControlName="idFatca">
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Obligaciones Fiscales: </span>
                        <!-- <fieldset> -->
                        <div class="category">
                            <label for="Id_oblifisS">Si</label>
                            <input id="Id_oblifisS" type="radio" maxlength="1" value="S" formControlName="oblifis">
                            <label for="Id_oblifisN">No</label>
                            <input id="Id_oblifisN" type="radio" maxlength="1" value="N" formControlName="oblifis">
                        </div>
                        <!-- </fieldset> -->
                    </div>
                    <div class="input-box">
                        <span class="details">Cantidad Obligaciones: </span>
                        <input type="text" maxlength="20" formControlName="cantobl">
                    </div>
                    <div class="input-box">
                        <span class="details">Descripción CRS: </span>
                        <input type="text" maxlength="20" formControlName="descriptionCRS">
                    </div>
                    <div class="subtitle">
                        <h3>Indicadores</h3>
                        <p *ngIf="ArrIdIndic.length == 0"> No hay indicadores para mostrar</p>
                    </div>
                    <div class="economic-details" *ngIf="ArrIdIndic.length != 0">
                        <div class="input-box">
                            <span>Id Indicador</span>
                            <input type="text" *ngFor="let IndicadorId of ArrIdIndic; let i = index" id="indicatorId{{i+1}}" [ngModel]="IndicadorId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="21" formControlName="indicatorIdI">
                            <input type="text" maxlength="21" formControlName="indicatorIdI2">
                            <input type="text" maxlength="21" formControlName="indicatorIdI3">
                            <input type="text" maxlength="21" formControlName="indicatorIdI4"> -->
                        </div>
                        <div class="input-box">
                            <span>Nombre</span>
                            <input type="text" *ngFor="let IndicadorName of ArrNamIndic; let i = index" id="name{{i+1}}" [ngModel]="IndicadorName" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="nameI">
                            <input type="text" maxlength="3" formControlName="nameI2">
                            <input type="text" maxlength="3" formControlName="nameI3">
                            <input type="text" maxlength="3" formControlName="nameI4"> -->
                        </div>
                        <div class="input-box">
                            <span>Valor</span>
                            <input type="text" *ngFor="let IndicadorValue of ArrValIndic; let i = index" id="value{{i+1}}" [ngModel]="IndicadorValue" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="valueI">
                            <input type="text" maxlength="3" formControlName="valueI2">
                            <input type="text" maxlength="3" formControlName="valueI3">
                            <input type="text" maxlength="3" formControlName="valueI4"> -->
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Obligaciones</h3>
                        <p *ngIf="ArrIdObli.length == 0"> No hay obligaciones para mostrar</p>
                    </div>
                    <div class="obligation-details" *ngIf="ArrIdObli.length != 0">
                        <div class="input-box">
                            <span>Código Obligación</span>
                            <input type="text" *ngFor="let ObligacionCode of ArrCodObli; let i = index" id="countryCode{{i+1}}" [ngModel]="ObligacionCode" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="21" formControlName="countryCode1">
                            <input type="text" maxlength="21" formControlName="countryCode2">
                            <input type="text" maxlength="21" formControlName="countryCode3">
                            <input type="text" maxlength="21" formControlName="countryCode4"> -->
                        </div>
                        <div class="input-box">
                            <span>Id Obligación</span>
                            <input type="text" *ngFor="let ObligacionId of ArrIdObli; let i = index" id="idObligation{{i+1}}" [ngModel]="ObligacionId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="idObligation1">
                            <input type="text" maxlength="3" formControlName="idObligation2">
                            <input type="text" maxlength="3" formControlName="idObligation3">
                            <input type="text" maxlength="3" formControlName="idObligation4"> -->
                        </div>
                    </div>
<!--                     <div class="data-date">
                        <div class="input-box">
                            <span class="details">Fecha Creación: </span>
                            <input type="text" formControlName="dateValueC">
                        </div>
                        <div class="input-box">
                            <span class="details">Fecha Modificación: </span>
                            <input type="text" formControlName="dateValueM">
                        </div>
                    </div> -->
                </div>
            </form>
            <form [formGroup]="fgConsultEconomic">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN ECONÓMICA</h2>
                    </div>
                    <div class="input-box-economic">
                        <span class="details">Actividad Económica: </span>
                        <input type="text" maxlength="40" formControlName="economicActivity">
                    </div>
                    <div class="input-box-economic">
                        <span class="details">Tipo Ocupación: </span>
                        <input type="text" maxlength="79" formControlName="occupationType">
                    </div>
                    <div class="economic-details">
                        <div class="input-box">
                            <span>Tipo Ingreso</span>
                            <input type="text" *ngFor="let IncomesId of ArrIdIncomes; let i = index" id="incomesType{{i+1}}" [ngModel]="IncomesId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Moneda</span>
                            <input type="text" *ngFor="let IncomCurrency of ArrCurrIncomes; let i = index" id="currency{{i+1}}" [ngModel]="IncomCurrency" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Monto</span>
                            <input type="text" *ngFor="let IncomAmount of ArrAmouIncomes; let i = index" id="amount{{i+1}}" [ngModel]="IncomAmount" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Información Laboral</h3>
                    </div>
                    <div class="input-box">
                      <span class="details">Puesto/Cargo: </span>
                      <input type="text" maxlength="18" formControlName="profesionPosition">
                    </div>
                    <div class="input-box">
                      <span class="details">Fecha Inicio </span>
                      <input type="text" maxlength="10" formControlName="workStartDate">
                    </div>
                    <div class="input-box">
                      <span class="details">Situación Laboral: </span>
                      <input type="text" maxlength="17" formControlName="employmentSituation">
                    </div>
                    <div class="input-box">
                      <span class="details">Actividad: </span>
                      <input type="text" formControlName="activity">
                    </div>
                </div>
            </form>
            <form [formGroup]="fgConsultAddress">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN DIRECCIONES</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Propiedad: </span>
                        <input type="text" maxlength="40" formControlName="addressType">
                    </div>
                    <div class="input-box">
                        <span class="details">País: </span>
                        <input type="text" maxlength="79" formControlName="country">
                    </div>
                    <div class="input-box">
                      <span class="details">Departamento: </span>
                      <input type="text" maxlength="18" formControlName="state">
                    </div>
                    <div class="input-box">
                      <span class="details">Ciudad: </span>
                      <input type="text" maxlength="10" formControlName="city">
                    </div>
                    <div class="input-box">
                      <span class="details">Dirección: </span>
                      <input type="text" maxlength="17" formControlName="addressName">
                    </div>
                    <div class="input-box">
                      <span class="details">Código Postal: </span>
                      <input type="text" formControlName="zipCode">
                    </div>
                    <div class="input-box">
                      <span class="details">Estrato </span>
                      <input type="text" formControlName="stratum">
                    </div>
                    <div class="input-box">
                      <span class="details">Antiguedad: </span>
                      <input type="text" formControlName="antiquity">
                    </div>
                    <div class="input-box" *ngIf="infoAdicional != ''">
                      <span class="details">Información Adicional</span>
                      <input type="text" formControlName="additionalInformation">
                    </div>
                </div>
            </form>
            <form [formGroup]="fgConsultContact">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN CONTACTOS</h2>
                    </div>
                    <div class="input-box-contact">
                        <span class="details">Contacto: </span>
                        <input type="text" *ngFor="let LCcontact of ArrLContact; let i = index" id="LCcontact{{i+1}}" [ngModel]="LCcontact" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box-contact">
                        <span class="details">Tipo Contacto: </span>
                        <input type="text" *ngFor="let LCtypecontact of ArrLContactType; let i = index" id="LCcontactType{{i+1}}" [ngModel]="LCtypecontact" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box-contact">
                      <span class="details">País: </span>
                      <input type="text" *ngFor="let LCcountry of ArrLCountry; let i = index" id="LCcountry{{i+1}}" [ngModel]="LCcountry" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box-contactCheck">
                        <span class="details">Preferencial: </span>
                        <input type="checkbox" *ngFor="let LCpreferencial of ArrLisPreferential; let i = index" id="LCpreferencial{{i+1}}" [ngModel]="LCpreferencial" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box-contactCheck">
                        <span class="details">Verificado: </span>
                        <input type="checkbox" *ngFor="let LCchecked of ArrLisChecked; let i = index" id="LCisChecked{{i+1}}" [ngModel]="LCchecked" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                </div>
            </form>
        </body>
    </div>
</div>
