import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CustomerService } from '../services/customer.service';
import { IModClient } from '../Interface/modClient';
import { ICreateClients } from '../Interface/createClient';
import { IConsultById } from '../Interface/consultById';
import { IConsultFatca } from '../Interface/consultFatca';
import { IListEconomic } from '../Interface/listEconomicDat';
import { IListAddress } from '../Interface/listAddress';
import { IListContact } from '../Interface/listContactDetails';

@Component({
  selector: 'app-mod-client',
  templateUrl: './mod-client.component.html',
  styleUrls: ['./mod-client.component.css']
})
export class ModClientComponent implements OnInit {
  @Input() IDClient! : string;
  @Input() DocNum! : string;
  dataModCustumer! : ICreateClients;
  submitted = false;
  errorserver = false;


  fgCustomer: FormGroup;
  fgConsultFatca: FormGroup;
  fgConsultEconomic: FormGroup;
  fgConsultAddress: FormGroup;

  dataConsultById!: IConsultById;
  dataConsultFatca!: IConsultFatca;
  dataListEconomic!: IListEconomic;
  dataListAddress!: IListAddress;

  infoAdicional!: string;
  typeNumDoc! : string;

  disableInputbox = true;
  ArrTypeContac: any[] = [];
  ArrContac: any[] = [];
  ArrCheckContac: any[] = [];
  ArrPrefeContac: any[] = [];
  ArrIdIndic: any[] = [];
  ArrNamIndic: any[] = [];
  ArrValIndic: any[] = [];
  ArrCodObli: any[] = [];
  ArrIdObli: any[] = [];
  ArrIdIncomes: any[] = [];
  ArrCurrIncomes: any[] = [];
  ArrAmouIncomes: any[] = [];

  ArrListDtd : any[] = [];
  ArrListDcn : any[] = [];
  ArrListDsn : any[] = [];
  ArrListDlc : any[] = [];
  ArrListDla : any[] = [];
  ArrListDlz : any[] = [];
  ArrListDls : any[] = [];
  ArrListDa : any[] = [];
  ArrListDai : any[] = [];

  constructor(
    private ServiceCustomer: CustomerService,
    private fbCustomer: FormBuilder,
    private fbConsultFatca: FormBuilder,
    private fbConsultEconomic: FormBuilder,
    private fbConsultAddress: FormBuilder
  ) {
    this.fgCustomer = this.fbCustomer.group({
      clientId: ['', Validators.required],
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      surnames: ['', Validators.required],
      birthDate: ['', Validators.required],
      cityBir: ['', Validators.required],
      nationalities: ['', Validators.required],
      documentTypeDT: ['', Validators.required],
      documentNumberDT: ['', Validators.required],
      cityDT: ['', Validators.required],
      addressNameD: ['', Validators.required],
      cityD: ['', Validators.required],
      stateD: ['', Validators.required],
      countryD: ['', Validators.required],
      membershipDate: ['', Validators.required],
      gender: ['', Validators.required],
      branch: ['', Validators.required],
      managerId: ['', Validators.required],
      customerType: ['', Validators.required],
      education: ['', Validators.required],
      profession: ['', Validators.required],
      cardname: ['', Validators.required],
      namePI: ['', Validators.required],
      documentTypePI: ['', Validators.required],
      documentNumberPI: ['', Validators.required],
      personsInCharge: ['', Validators.required],
    });

    this.fgConsultFatca = this.fbConsultFatca.group({
      idFatca: ['', Validators.required],
      oblifis: ['', Validators.required],
      cantobl: ['', Validators.required],
      descriptionCRS: ['', Validators.required]
    });

    this.fgConsultEconomic = this.fbConsultEconomic.group({
      economicActivity: ['', Validators.required],
      occupationType: ['', Validators.required],
      profesionPosition: ['', Validators.required],
      workStartDate: ['', Validators.required],
      employmentSituation: ['', Validators.required],
      activity: ['', Validators.required],
    });

    this.fgConsultAddress = this.fbConsultAddress.group({
      addressType: ['', Validators.required],
      country: ['', Validators.required],
      state: ['', Validators.required],
      city: ['', Validators.required],
      addressName: ['', Validators.required],
      zipCode: ['', Validators.required],
      stratum: ['', Validators.required],
      antiquity: ['', Validators.required],
      additionalInformation: ['', Validators.required],
    });

  }

  ngOnInit(): void {
    this.DocNum
    this.getCustomersNew();
  }

  async getCustomersNew() {
    console.log('ask someone if getCustomersxId work');
    console.log(this.IDClient);
    this.ServiceCustomer.getCustomerById(this.IDClient).subscribe(async (dataConsultById) => {
      console.log('CONSULTA PERSONA');
      console.log(dataConsultById);   

      let lasname;
      if (dataConsultById['data']['surnames']) {
        lasname = dataConsultById['data']['surnames'];
      } else {
        lasname = '';
      }

      let IDmanager;
      if (dataConsultById['data']['manager']['managerId']) {
        IDmanager = dataConsultById['data']['manager']['managerId'];
      } else {
        IDmanager = '';
      }

      let PIDname ;
      if (dataConsultById['data']['partnerInformation']['name']) {
        PIDname = dataConsultById['data']['partnerInformation']['name'];
      } else {
        PIDname = '';
      }

      let PIDDTname ;
      if (dataConsultById['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name']) {
        PIDDTname = dataConsultById['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name'];
      } else {
        PIDDTname = '';
      }

      let PIDdocNumber ;
      if (dataConsultById['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber']) {
        PIDdocNumber = dataConsultById['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber'];
      } else {
        PIDdocNumber = '';
      }


      let Tam = dataConsultById['data']['contactDetails'].length;
      let cont = 0;
      while (cont <= Tam-1) {
        let tc = dataConsultById['data']['contactDetails'][cont]['contactType']['name'];
        let tempTc = this.ArrTypeContac.push(tc);
        let nc = dataConsultById['data']['contactDetails'][cont]['contact'];
        let tempNc = this.ArrContac.push(nc);
        let pc = dataConsultById['data']['contactDetails'][cont]['isPreferential'];
        let tempPc = this.ArrPrefeContac.push(pc);
        let vc = dataConsultById['data']['contactDetails'][cont]['isChecked'];
        let tempVc = this.ArrCheckContac.push(vc);
        cont++;
      }

      this.fgCustomer.setValue({
        clientId: dataConsultById['data']['clientId'],
        firstName: dataConsultById['data']['firstName'],
        lastName: dataConsultById['data']['lastName'],
        surnames: lasname,
        birthDate: dataConsultById['data']['birthDate'],
        cityBir: dataConsultById['data']['identityDocuments'][0]['city'],
        nationalities: dataConsultById['data']['nationalities'][0]['id'],
        documentTypeDT: dataConsultById['data']['identityDocuments'][0]['documentType']['name'],
        documentNumberDT: dataConsultById['data']['identityDocuments'][0]['documentNumber'],
        cityDT: dataConsultById['data']['identityDocuments'][0]['city'],
        addressNameD: dataConsultById['data']['legalAddress']['addressName'],
        cityD: dataConsultById['data']['legalAddress']['city'],
        stateD: dataConsultById['data']['legalAddress']['state'],
        countryD: dataConsultById['data']['legalAddress']['country']['name'],
        membershipDate: dataConsultById['data']['membershipDate'],
        gender: dataConsultById['data']['gender']['id'],
        branch: dataConsultById['data']['bank']['branch']['name'],
        managerId: IDmanager,
        customerType: dataConsultById['data']['customerType']['name'],
        education: dataConsultById['data']['education']['level'],
        profession: dataConsultById['data']['profession'],
        cardname: dataConsultById['data']['cardName'],
        namePI: PIDname,
        documentTypePI: PIDDTname,
        documentNumberPI: PIDdocNumber,
        personsInCharge: dataConsultById['data']['personsInCharge'],
      });

    });

    this.ServiceCustomer.getFatcaById(this.IDClient).subscribe((dataConsultFatca) => {
      if (dataConsultFatca['indicators'].length != 0) {
        let valorIndicador;
        let Tam = dataConsultFatca['indicators'].length;
        let cont = 0;
        while (cont <= Tam - 1) {
          let iId = dataConsultFatca['indicators'][cont]['indicatorId'];
          let tempIi = this.ArrIdIndic.push(iId);
          let iNa = dataConsultFatca['indicators'][cont]['name'];
          let tempNa = this.ArrNamIndic.push(iNa);
          let iVal = dataConsultFatca['indicators'][cont]['value'];
          if (iVal) {
            valorIndicador = dataConsultFatca['indicators'][cont]['value'];
          } else {
            valorIndicador = '';
          }
          let tempIval = this.ArrValIndic.push(valorIndicador);
          cont++;
        }
      }

      if (dataConsultFatca['obligation'].length != 0) {
        let tamObli = dataConsultFatca['obligation'].length;
        let contObli = 0;
        while (contObli <= tamObli - 1) {
          let oCc = dataConsultFatca['obligation'][contObli]['countryCode'];
          let tempOcc = this.ArrCodObli.push(oCc);
          let oId = dataConsultFatca['obligation'][contObli]['idObligation'];
          let tempOid = this.ArrIdObli.push(oId);
          contObli++;
        }
      }

      this.fgConsultFatca.setValue({
        idFatca: dataConsultFatca['idFatca'],
        oblifis: dataConsultFatca['oblifis'],
        cantobl: dataConsultFatca['cantobl'],
        descriptionCRS: dataConsultFatca['descriptionCRS']
      });
    });

    this.ServiceCustomer.getEconomicById(this.DocNum).subscribe((dataListEconomic) => {
      if (dataListEconomic['data']['incomes'].length != 0) {
        let TamI = dataListEconomic['data']['incomes'].length;
        let contI = 0;
        while (contI <= TamI - 1) {
          let iIdT = dataListEconomic['data']['incomes'][contI]['incomesType']['id'];
          let tempIid = this.ArrIdIncomes.push(iIdT);
          let iCu = dataListEconomic['data']['incomes'][contI]['incomesValue']['currency'];
          let tempCu = this.ArrCurrIncomes.push(iCu);
          let iAm = dataListEconomic['data']['incomes'][contI]['incomesValue']['amount'];
          let tempIam = this.ArrAmouIncomes.push(iAm);
          contI++
        } 
      }

      this.fgConsultEconomic.setValue({
        economicActivity: dataListEconomic['data']['economicActivity']['name'],
        occupationType: dataListEconomic['data']['occupationType']['name'],
        profesionPosition: dataListEconomic['data']['workPlace']['profesionPosition']['name'],
        workStartDate: dataListEconomic['data']['workPlace']['workStartDate'],
        employmentSituation: dataListEconomic['data']['employmentSituation'],
        activity: dataListEconomic['data']['workPlace']['activity'],
      });
    });

    this.ServiceCustomer.getAddressById(this.DocNum).subscribe((dataListAddress) => {
      /* console.log('CONSULTA DIRECCIONES');
      console.log(dataListAddress); */
      this.infoAdicional;
      if (dataListAddress['data'][0]['location']['additionalInformation']) {
        this.infoAdicional =
          dataListAddress['data'][0]['location']['additionalInformation'];
      } else {
        this.infoAdicional = '';
      }

      if (dataListAddress['data'].length != 0) {
        let tamLD = dataListAddress['data'].length;
        let contLD = 0;

        while (contLD <= tamLD - 1) {             
          let LDtd = dataListAddress['data'][contLD]['addressType']['name'];
          let tempLDtd = this.ArrListDtd.push(LDtd);
          let LDcn = dataListAddress['data'][contLD]['location']['country']['name'];
          let tempLDcn = this.ArrListDcn.push(LDcn);
          let LDsn = dataListAddress['data'][contLD]['location']['state']['name'];
          let tempLDsn = this.ArrListDsn.push(LDsn);
          let LDlc = dataListAddress['data'][contLD]['location']['city'];
          let tempLDlc = this.ArrListDlc.push(LDlc);
          let LDla = dataListAddress['data'][contLD]['location']['addressName'];
          let tempLDla = this.ArrListDla.push(LDla);
          let LDlz = dataListAddress['data'][contLD]['location']['zipCode'];
          let tempLDlz = this.ArrListDlz.push(LDlz);
          let LDls = dataListAddress['data'][contLD]['location']['stratum'];
          let tempLDls = this.ArrListDls.push(LDls);        
          let LDa = dataListAddress['data'][contLD]['antiquity'];
          let tempLDa = this.ArrListDa.push(LDa);  
          let LDia = this.infoAdicional;
          let tempLDia = this.ArrListDai.push(LDia);  
          contLD++
        } 

      }
    });

  }

  updateClient(){
    console.log('ask someone if getupdateClient work');

    const dataModClient : ICreateClients = {
      firstName: this.fgCustomer.value.firstName, 
      lastName: this.fgCustomer.value.lastName,
      surnames: this.fgCustomer.value.surnames,
      gender: { id: this.fgCustomer.value.gender },
      bank: {
        branch: { id: this.fgCustomer.value.branch },
        id: this.fgCustomer.value.bank,
      },
      birthData: {
        country: { id: this.fgCustomer.value.countryBir },
        birthDate: this.fgCustomer.value.birthDate,
        state: { id: this.fgCustomer.value.stateBir },
        city: this.fgCustomer.value.cityBir,
      },
      nationalities: [{ id: this.fgCustomer.value.nationalities }],
      identityDocuments: [
        {
          documentType: { id: this.fgCustomer.value.documentTypeDT },
          country: { id: this.fgCustomer.value.countryDT },
          city: this.fgCustomer.value.cityDT,
          state: { id: this.fgCustomer.value.stateDT },
          expirationDate: this.fgCustomer.value.expirationDate,
          documentNumber: this.fgCustomer.value.documentNumberDT,
        },
      ],
      maritalStatus: { id: this.fgCustomer.value.maritalStatus },
      contactDetails: [
        {
          contact: this.fgCustomer.value.contact,
          contactType: { id: "MOBILE_NUMBER" },
        },
        {
          contact: this.fgCustomer.value.contact2,
          contactType: { id: "EMAIL" },
        },
        {
          contact: this.fgCustomer.value.contact3,
          contactType: { id: "OFFICE_NUMBER" },
        },
      ],
      economicData: {
        economicActivity: { id: this.fgCustomer.value.economicActivity },
        occupationType: { id: this.fgCustomer.value.occupationType },
        employmentSituation: this.fgCustomer.value.employmentSituation,
        incomes: [
          {
            incomesType: { id: this.fgCustomer.value.incomesType },
            incomesValue: {
              currency: this.fgCustomer.value.currency,
              amount: this.fgCustomer.value.amount,
            },
          },
        ],
        workPlace: {
          name: this.fgCustomer.value.nameWP,
          taxpayerIdentification: {
            documentType: { id: this.fgCustomer.value.documentTypeWP },
            documentNumber: this.fgCustomer.value.documentNumberWP,
          },
          workStartDate: this.fgCustomer.value.workStartDateWP,
          address: {
            location: {
              addressName: this.fgCustomer.value.addressNameWP,
              country: { id: this.fgCustomer.value.countryWP },
              state: { id: this.fgCustomer.value.stateWP },
              city: this.fgCustomer.value.cityWP,
            },
          },
        },
      },
      addresses: [
        {
          location: {
            addressName: this.fgCustomer.value.addressNameD,
            city: this.fgCustomer.value.cityD,
            state: { id: this.fgCustomer.value.stateD },
            country: { id: this.fgCustomer.value.countryD },
          },
        },
      ],
      personsInCharge: this.fgCustomer.value.personsInCharge,
    };
    this.submitted = true;

    this.ServiceCustomer.updateCustomer(this.DocNum,dataModClient).subscribe(data => {
      console.log(data);
    }, error => {
      this.errorserver = true;
      console.log("¡¡Cliente NO ACTUALIZADO!!");
    });

  }

  closeUpdate(){
    this.ServiceCustomer.$modalModify.emit(false); 
  }

}
<div id="global" class="modal">
    <div class="container-modal">
        <body class="body-consult">
            <form [formGroup]="fgCustomer" (ngSubmit)="updateClient()">
                <div class="button-close">
                    <button type="button" (click)="closeUpdate()"><img src="https://cdn-icons-png.flaticon.com/512/9134/9134450.png"> </button>
                </div>
                <div class="user-details">
                    <div class="subtitle">
                        <h2>DATOS BÁSICOS</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Cliente: </span>
                        <input type="text" maxlength="16" formControlName="customerType">
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación Interna: </span>
                        <input type="text" maxlength="16" formControlName="clientId">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="16" formControlName="documentTypeDT">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('documentTypeDT')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Número Identificación: </span>
                        <input type="text"  maxlength="15" formControlName="documentNumberDT">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('documentNumberDT')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Nombres: </span>
                        <input type="text" maxlength="20" formControlName="firstName">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('firstName')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Primer Apellido: </span>
                        <input type="text" maxlength="20" formControlName="lastName">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('lastName')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Segundo Apellido: </span>
                        <input type="text" maxlength="20" formControlName="surnames">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('surnames')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Género: </span>
                            <div class="category">
                                <label for="Id_genderF">F</label>
                                <input id="Id_genderF" type="radio" maxlength="1" value="F" formControlName="gender">
                                <label for="Id_genderM">M</label>
                                <input id="Id_genderM" type="radio" maxlength="1" value="M" formControlName="gender">
                            </div>
                            <div class="error" *ngIf="submitted && this.fgCustomer.get('gender')?.errors?.['required']">Campo Obligatorio</div>
                    </div>  
                    <div class="input-box">
                        <span class="details">Fecha Nacimiento: </span>
                        <input type="text" maxlength="10" formControlName="birthDate">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('gender')?.errors?.['required']">Campo Obligatorio</div>
                    </div>   
                    <div class="input-box">
                        <span class="details">Ciudad Expedición: </span>
                        <input type="text" maxlength="4" formControlName="cityBir">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('cityBir')?.errors?.['required']">Campo Obligatorio</div>
                    </div>                                                      
                    <div class="input-box">
                        <span class="details">Nacionalidad: </span>
                        <input type="text" maxlength="4" formControlName="nationalities">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('nationalities')?.errors?.['required']">Campo Obligatorio</div>
                    </div>  
                    <div class="input-box">
                        <span class="details">Personas a Cargo: </span>
                        <input type="number" maxlength="2" formControlName="personsInCharge">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('personsInCharge')?.errors?.['required']">Campo Obligatorio</div>
                    </div>    
                    <div class="input-box">
                        <span class="details">Profesión: </span>
                        <input type="text" maxlength="2" formControlName="profession">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('profession')?.errors?.['required']">Campo Obligatorio</div>
                    </div> 
                    <div class="input-box">
                        <span class="details">Nivel Educativo: </span>
                        <input type="text" maxlength="2" formControlName="education">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('education')?.errors?.['required']">Campo Obligatorio</div>
                    </div>   
                    <div class="subtitle">
                        <h2>INFORMACIÓN DEL BANCO</h2>
                    </div>                           
                    <div class="input-box">
                        <span class="details">Sucursal: </span>
                        <input type="text" maxlength="4" formControlName="branch">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('branch')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Membresía: </span>
                        <input type="text" maxlength="10" formControlName="membershipDate">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('membershipDate')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Tarjeta: </span>
                        <input type="text" maxlength="26" formControlName="cardname">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('cardname')?.errors?.['required']">Campo Obligatorio</div>
                    </div> 
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE RESIDENCIA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">País Residencia: </span>
                        <input type="text" maxlength="3" formControlName="countryD">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('countryD')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Departamento Residencia: </span>
                        <input type="text" maxlength="4" formControlName="stateD">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('stateD')?.errors?.['required']">Campo Obligatorio</div>
                    </div>  
                    <div class="input-box">
                        <span class="details">Ciudad Residencia: </span>
                        <input type="text" maxlength="20" formControlName="cityD">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('cityD')?.errors?.['required']">Campo Obligatorio</div>
                    </div> 
                    <div class="input-box-economic">
                        <span class="details">Dirección Residencia: </span>
                        <input type="text" maxlength="50" formControlName="addressNameD">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('addressNameD')?.errors?.['required']">Campo Obligatorio</div>
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE CONTACTO</h2>
                    </div>
                    <div class="contact-details">
                        <div class="input-box">
                            <span>Tipo</span>
                            <input type="text" maxlength="20" *ngFor="let TypeC of ArrTypeContac; let i = index" id="contactType{{i+1}}" [ngModel]="TypeC" [ngModelOptions]="{standalone: true}">
                            <div class="error" *ngIf="submitted && this.fgCustomer.get('contactType{{i+1}}')?.errors?.['required']">Campo Obligatorio</div>
                        </div>
                        <div class="input-box">
                            <span>Número</span>
                            <input type="text" maxlength="80" *ngFor="let NumC of ArrContac; let i = index" id="contact{{i+1}}" [ngModel]="NumC" [ngModelOptions]="{standalone: true}">
                            <div class="error" *ngIf="submitted && this.fgCustomer.get('contact{{i+1}}')?.errors?.['required']">Campo Obligatorio</div>
                        </div>
                        <div class="input-box-contactCheck">
                            <span class="details">Preferencial: </span>
                            <input type="checkbox" *ngFor="let PrefC of ArrPrefeContac; let i = index" id="LCpreferencial{{i+1}}" [ngModel]="PrefC" [ngModelOptions]="{standalone: true}">
                            <div class="error" *ngIf="submitted && this.fgCustomer.get('LCpreferencial{{i+1}}')?.errors?.['required']">Campo Obligatorio</div>
                        </div>
                        <div class="input-box-contactCheck">
                            <span class="details">Verificado: </span>
                            <input type="checkbox" *ngFor="let VerC of ArrCheckContac; let i = index" id="LCisChecked{{i+1}}" [ngModel]="VerC" [ngModelOptions]="{standalone: true}">
                            <div class="error" *ngIf="submitted && this.fgCustomer.get('LCisChecked{{i+1}}')?.errors?.['required']">Campo Obligatorio</div>
                        </div>
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DEL SOCIO</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="20" formControlName="documentTypePI">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('documentTypePI')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación: </span>
                        <input type="text" maxlength="15" formControlName="documentNumberPI">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('documentNumberPI')?.errors?.['required']">Campo Obligatorio</div>
                    </div>
                    <div class="input-box">
                        <span class="details">Nombre: </span>
                        <input type="text" maxlength="20" formControlName="namePI">
                        <div class="error" *ngIf="submitted && this.fgCustomer.get('namePI')?.errors?.['required']">Campo Obligatorio</div>
                    </div>                  
                </div>
                <div class="button">
                    <input type="submit" value="Actualizar">
                </div>
            </form>
            <form [formGroup]="fgConsultFatca">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>CONSULTA FATCA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Id Fatca: </span>
                        <input type="text" maxlength="16" formControlName="idFatca">
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Obligaciones Fiscales: </span>
                        <div class="category">
                            <label for="Id_oblifisS">Si</label>
                            <input id="Id_oblifisS" type="radio" maxlength="1" value="S" formControlName="oblifis">
                            <label for="Id_oblifisN">No</label>
                            <input id="Id_oblifisN" type="radio" maxlength="1" value="N" formControlName="oblifis">
                        </div>
                    </div>
                    <div class="input-box">
                        <span class="details">Cantidad Obligaciones: </span>
                        <input type="text" maxlength="20" formControlName="cantobl">
                    </div>
                    <div class="input-box">
                        <span class="details">Descripción CRS: </span>
                        <input type="text" maxlength="20" formControlName="descriptionCRS">
                    </div>
                    <div class="subtitle">
                        <h3>Indicadores</h3>
                        <p *ngIf="ArrIdIndic.length == 0"> No hay indicadores para mostrar</p>
                    </div>
                    <div class="economic-details" *ngIf="ArrIdIndic.length != 0">
                        <div class="input-box">
                            <span>Id Indicador</span>
                            <input type="text" *ngFor="let IndicadorId of ArrIdIndic; let i = index" id="indicatorId{{i+1}}" [ngModel]="IndicadorId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Nombre</span>
                            <input type="text" *ngFor="let IndicadorName of ArrNamIndic; let i = index" id="name{{i+1}}" [ngModel]="IndicadorName" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Valor</span>
                            <input type="text" *ngFor="let IndicadorValue of ArrValIndic; let i = index" id="value{{i+1}}" [ngModel]="IndicadorValue" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Obligaciones</h3>
                        <p *ngIf="ArrIdObli.length == 0"> No hay obligaciones para mostrar</p>
                    </div>
                    <div class="obligation-details" *ngIf="ArrIdObli.length != 0">
                        <div class="input-box">
                            <span>Código Obligación</span>
                            <input type="text" *ngFor="let ObligacionCode of ArrCodObli; let i = index" id="countryCode{{i+1}}" [ngModel]="ObligacionCode" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Id Obligación</span>
                            <input type="text" *ngFor="let ObligacionId of ArrIdObli; let i = index" id="idObligation{{i+1}}" [ngModel]="ObligacionId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                    </div>
                </div>
                <div class="button">
                    <input type="submit" value="Actualizar">
                </div>
            </form>
            <form [formGroup]="fgConsultEconomic">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN ECONÓMICA</h2>
                    </div>
                    <div class="input-box-economic">
                        <span class="details">Actividad Económica: </span>
                        <input type="text" maxlength="40" formControlName="economicActivity">
                    </div>
                    <div class="input-box-economic">
                        <span class="details">Tipo Ocupación: </span>
                        <input type="text" maxlength="79" formControlName="occupationType">
                    </div>
                    <div class="economic-details">
                        <div class="input-box">
                            <span>Tipo Ingreso</span>
                            <input type="text" *ngFor="let IncomesId of ArrIdIncomes; let i = index" id="incomesType{{i+1}}" [ngModel]="IncomesId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Moneda</span>
                            <input type="text" *ngFor="let IncomCurrency of ArrCurrIncomes; let i = index" id="currency{{i+1}}" [ngModel]="IncomCurrency" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                        <div class="input-box">
                            <span>Monto</span>
                            <input type="text" *ngFor="let IncomAmount of ArrAmouIncomes; let i = index" id="amount{{i+1}}" [ngModel]="IncomAmount" [ngModelOptions]="{standalone: true}" [disabled]="true">
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Información Laboral</h3>
                    </div>
                    <div class="input-box">
                      <span class="details">Puesto/Cargo: </span>
                      <input type="text" maxlength="18" formControlName="profesionPosition">
                    </div>
                    <div class="input-box">
                      <span class="details">Fecha Inicio </span>
                      <input type="text" maxlength="10" formControlName="workStartDate">
                    </div>
                    <div class="input-box">
                      <span class="details">Situación Laboral: </span>
                      <input type="text" maxlength="17" formControlName="employmentSituation">
                    </div>
                    <div class="input-box">
                      <span class="details">Actividad: </span>
                      <input type="text" formControlName="activity">
                    </div>
                </div>
                <div class="button">
                    <input type="submit" value="Actualizar">
                </div>
            </form>           
            <form [formGroup]="fgConsultAddress">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN DIRECCIONES</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Propiedad: </span>
                        <input type="text" *ngFor="let LDaddressType of ArrListDtd; let i = index ;" id="LDaddressType{{i+1}}" [ngModel]="LDaddressType" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                        <span class="details">País: </span>
                        <input type="text" *ngFor="let LDcountry of ArrListDcn; let i = index ;" id="LDcountry{{i+1}}" [ngModel]="LDcountry" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Departamento: </span>
                      <input type="text" *ngFor="let LDstate of ArrListDsn; let i = index ;" id="LDstate{{i+1}}" [ngModel]="LDstate" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Ciudad: </span>
                      <input type="text" *ngFor="let LDcity of ArrListDlc; let i = index ;" id="LDcity{{i+1}}" [ngModel]="LDcity" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Dirección: </span>
                      <input type="text" *ngFor="let LDaddressName of ArrListDla; let i = index ;" id="LDaddressName{{i+1}}" [ngModel]="LDaddressName" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Código Postal: </span>
                      <input type="text" *ngFor="let LDzipCode of ArrListDlz; let i = index ;" id="LDzipCode{{i+1}}" [ngModel]="LDzipCode" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Estrato </span>
                      <input type="text" *ngFor="let LDstratum of ArrListDls; let i = index ;" id="LDstratum{{i+1}}" [ngModel]="LDstratum" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box">
                      <span class="details">Antiguedad: </span>
                      <input type="text" *ngFor="let LDantiquity of ArrListDa; let i = index ;" id="LDantiquity{{i+1}}" [ngModel]="LDantiquity" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                    <div class="input-box" *ngIf="infoAdicional != ''">
                      <span class="details">Información Adicional</span>
                      <input type="text" *ngFor="let LDadditionalInformation of ArrListDai; let i = index ;" id="LDadditionalInformation{{i+1}}" [ngModel]="LDadditionalInformation" [ngModelOptions]="{standalone: true}" [disabled]="true">
                    </div>
                </div>
                <div class="button">
                    <input type="submit" value="Actualizar">
                </div>
            </form>
        </body>
    </div>
</div>
