import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CustomerService } from '../services/customer.service';
import { IModClient } from '../Interface/modClient';
import { ICreateClients } from '../Interface/createClient';
import { IConsultById } from '../Interface/consultById';

@Component({
  selector: 'app-mod-client',
  templateUrl: './mod-client.component.html',
  styleUrls: ['./mod-client.component.css']
})
export class ModClientComponent implements OnInit {
  @Input() IDClient! : string;
  upClient : FormGroup;
  dataModCustumer! : ICreateClients;
  submitted = false;
  errorserver = false;
  DocNum! : string;

  ArrTypeContac: any[] = [];
  ArrContac: any[] = [];
  ArrCheckContac: any[] = [];
  ArrPrefeContac: any[] = [];

  constructor(private ServiceCustomer : CustomerService, private fbModClient : FormBuilder) { 
    this.upClient = this.fbModClient.group({
      firstName: [''], 
      lastName: [''],
      surnames: ['', ],
      gender: [''],
      bank: [''],
      branch: [''],
      countryBir: [''],
      birthDate: [''],
      stateBir: [''],
      cityBir: [''],
      nationalities: [''],
      documentTypeDT: [''],
      countryDT: [''],
      cityDT: [''],
      stateDT: [''],
      expirationDate: [''],
      documentNumberDT: [''],
      maritalStatus: [''],
      contact: [''],
      contact2: [''],
      contact3: [''],
/*      economicActivity: [''],
       occupationType: [''],
      employmentSituation: [''],
      incomesType: [''],
      currency: [''],
      amount: [''], */
/*       nameWP: [''],
      documentTypeWP: [''],
      documentNumberWP: [''],
      workStartDateWP: [''],
      addressNameWP: [''],
      countryWP: [''],
      stateWP: [''],
      cityWP: [''], */
      addressNameD: [''],
      cityD: [''],
      stateD: [''],
      countryD: [''],
      personsInCharge: [''],
    });
  }

  ngOnInit(): void {
    this.getCustomersNew();
  }

  async getCustomersNew(){
    console.log('ask someone if getupClient work');
    console.log(this.IDClient);
    this.ServiceCustomer.getCustomerById(this.IDClient).subscribe(async (dataModCustumer) => {
      console.log('CONSULTA PERSONA');
      console.log(dataModCustumer);   

      let lasname;
      if (dataModCustumer['data']['surnames']) {
        lasname = dataModCustumer['data']['surnames'];
      } else {
        lasname = '';
      }

      let TipoDoc;
      let NumDoc = dataModCustumer['data']['identityDocuments'][0]['documentNumber'];
      if(dataModCustumer['data']['identityDocuments'][0]['documentType']['id'] == 'CC'){
        TipoDoc = "1"
      }else{
        TipoDoc = ""
      }

      this.DocNum = TipoDoc+NumDoc; 

      let IDmanager;
      if (dataModCustumer['data']['manager']['managerId']) {
        IDmanager = dataModCustumer['data']['manager']['managerId'];
      } else {
        IDmanager = '';
      }

      let PIDname ;
      if (dataModCustumer['data']['partnerInformation']['name']) {
        PIDname = dataModCustumer['data']['partnerInformation']['name'];
      } else {
        PIDname = '';
      }

      let PIDDTname ;
      if (dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name']) {
        PIDDTname = dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name'];
      } else {
        PIDDTname = '';
      }

      let PIDdocNumber ;
      if (dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber']) {
        PIDdocNumber = dataModCustumer['data']['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber'];
      } else {
        PIDdocNumber = '';
      }


      let Tam = dataModCustumer['data']['contactDetails'].length;
      let cont = 0;
      while (cont <= Tam-1) {
        let tc = dataModCustumer['data']['contactDetails'][cont]['contactType']['name'];
        let tempTc = this.ArrTypeContac.push(tc);
        let nc = dataModCustumer['data']['contactDetails'][cont]['contact'];
        let tempNc = this.ArrContac.push(nc);
        let pc = dataModCustumer['data']['contactDetails'][cont]['isPreferential'];
        let tempPc = this.ArrPrefeContac.push(pc);
        let vc = dataModCustumer['data']['contactDetails'][cont]['isChecked'];
        let tempVc = this.ArrCheckContac.push(vc);
        cont++;
      }

      this.upClient.setValue({
        firstName: dataModCustumer['data']['firstName'],
        lastName: dataModCustumer['data']['lastName'],
        surnames: lasname,
        birthDate: dataModCustumer['data']['birthDate'],
        cityBir: dataModCustumer['data']['identityDocuments'][0]['city'],
        countryBir:'',
        stateBir: '',
        nationalities: dataModCustumer['data']['nationalities'][0]['id'],
        documentTypeDT: dataModCustumer['data']['identityDocuments'][0]['documentType']['name'],
        documentNumberDT: dataModCustumer['data']['identityDocuments'][0]['documentNumber'],
        countryDT:'',
        stateDT:'',
        expirationDate:'',
        maritalStatus:'',
        contact: '',
        contact2: '',
        contact3: '',
        cityDT: dataModCustumer['data']['identityDocuments'][0]['city'],
        addressNameD: dataModCustumer['data']['legalAddress']['addressName'],
        cityD: dataModCustumer['data']['legalAddress']['city'],
        stateD: dataModCustumer['data']['legalAddress']['state'],
        countryD: dataModCustumer['data']['legalAddress']['country']['name'],
        membershipDate: dataModCustumer['data']['membershipDate'],
        gender: dataModCustumer['data']['gender']['id'],
        bank: '',
        branch: dataModCustumer['data']['bank']['branch']['name'],
        managerId: IDmanager,
        customerType: dataModCustumer['data']['customerType']['name'],
        education: dataModCustumer['data']['education']['level'],
        profession: dataModCustumer['data']['profession'],
        cardname: dataModCustumer['data']['cardName'],
        namePI: PIDname,
        documentTypePI: PIDDTname,
        documentNumberPI: PIDdocNumber,
        personsInCharge: dataModCustumer['data']['personsInCharge'],
      });    

    });
    console.log(this.DocNum);

    return this.DocNum;
  }

  updateClient(){
    console.log('ask someone if getupdateClient work');

    const dataClient : ICreateClients = {
      firstName: this.upClient.value.firstName, 
      lastName: this.upClient.value.lastName,
      surnames: this.upClient.value.surnames,
      gender: { id: this.upClient.value.gender },
      bank: {
        branch: { id: this.upClient.value.branch },
        id: this.upClient.value.bank,
      },
      birthData: {
        country: { id: this.upClient.value.countryBir },
        birthDate: this.upClient.value.birthDate,
        state: { id: this.upClient.value.stateBir },
        city: this.upClient.value.cityBir,
      },
      nationalities: [{ id: this.upClient.value.nationalities }],
      identityDocuments: [
        {
          documentType: { id: this.upClient.value.documentTypeDT },
          country: { id: this.upClient.value.countryDT },
          city: this.upClient.value.cityDT,
          state: { id: this.upClient.value.stateDT },
          expirationDate: this.upClient.value.expirationDate,
          documentNumber: this.upClient.value.documentNumberDT,
        },
      ],
      maritalStatus: { id: this.upClient.value.maritalStatus },
      contactDetails: [
        {
          contact: this.upClient.value.contact,
          contactType: { id: "MOBILE_NUMBER" },
        },
        {
          contact: this.upClient.value.contact2,
          contactType: { id: "EMAIL" },
        },
        {
          contact: this.upClient.value.contact3,
          contactType: { id: "OFFICE_NUMBER" },
        },
      ],
      economicData: {
        economicActivity: { id: this.upClient.value.economicActivity },
        occupationType: { id: this.upClient.value.occupationType },
        employmentSituation: this.upClient.value.employmentSituation,
        incomes: [
          {
            incomesType: { id: this.upClient.value.incomesType },
            incomesValue: {
              currency: this.upClient.value.currency,
              amount: this.upClient.value.amount,
            },
          },
        ],
        workPlace: {
          name: this.upClient.value.nameWP,
          taxpayerIdentification: {
            documentType: { id: this.upClient.value.documentTypeWP },
            documentNumber: this.upClient.value.documentNumberWP,
          },
          workStartDate: this.upClient.value.workStartDateWP,
          address: {
            location: {
              addressName: this.upClient.value.addressNameWP,
              country: { id: this.upClient.value.countryWP },
              state: { id: this.upClient.value.stateWP },
              city: this.upClient.value.cityWP,
            },
          },
        },
      },
      addresses: [
        {
          location: {
            addressName: this.upClient.value.addressNameD,
            city: this.upClient.value.cityD,
            state: { id: this.upClient.value.stateD },
            country: { id: this.upClient.value.countryD },
          },
        },
      ],
      personsInCharge: this.upClient.value.personsInCharge,
    };
    this.submitted = true;

/*     if(this.upClient.valid){
      this.stateValid = "Formulario Completo";
    }else{
      this.stateValid = "Formulario Incompleto";
      return;
    } */

    this.ServiceCustomer.updateCustomer(this.DocNum,dataClient).subscribe(data => {
      console.log(data);
    }, error => {
      this.errorserver = true;
      console.log("¡¡Cliente NO ACTUALIZADO!!");
    });

  }

  closeUpdate(){
    this.ServiceCustomer.$modalModify.emit(false); 
  }

}
