import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CustomerService } from '../services/customer.service';
import { IConsultById } from '../Interface/consultById';
import { IConsultFatca } from '../Interface/consultFatca';
import { IListEconomic } from '../Interface/listEconomicDat';
import { IListAddress } from '../Interface/listAddress';
import { IListContact } from '../Interface/listContactDetails';

@Component({
  selector: 'app-consult-client',
  templateUrl: './consult-client.component.html',
  styleUrls: ['./consult-client.component.css']
})
export class ConsultClientComponent implements OnInit {
  fgCustomer : FormGroup;
  fgConsultFatca : FormGroup;
  fgConsultEconomic : FormGroup;
  fgConsultAddress : FormGroup;  
  fgConsultContact : FormGroup;

  dataConsultById!: IConsultById;
  dataConsultFatca!: IConsultFatca;
  dataListEconomic!: IListEconomic;
  dataListAddress! : IListAddress;
  dataListContact! : IListContact;

  infoAdicional! : string;

  disableInputbox = true;
  ArrTypeContac: any[] = [];
  ArrContac: any[] = [];
  ArrCheckContac: any[] = [];
  ArrPrefeContac: any[] = [];
  ArrIdIndic : any[] = [];
  ArrNamIndic : any[] = [];
  ArrValIndic : any[] = [];
  ArrCodObli : any[] = [];
  ArrIdObli : any[] = [];
  
  constructor(
    private ServiceCustomer: CustomerService, 
    private fbCustomer: FormBuilder, 
    private fbConsultFatca : FormBuilder,
    private fbConsultEconomic : FormBuilder,
    private fbConsultAddress : FormBuilder,
    private fbConsultContact : FormBuilder) {

    this.fgCustomer = this.fbCustomer.group({
      clientId : new FormControl({value: '',disabled: true}),
      firstName : new FormControl({value: '',disabled: true}),
      lastName : new FormControl({value: '',disabled: true}),
      surnames :  new FormControl({value: '',disabled: true}),
      birthDate : new FormControl({value: '',disabled: true}),
      cityBir : new FormControl({value: '',disabled: true}),
      nationalities : new FormControl({value: '',disabled: true}),
      documentTypeDT : new FormControl({value: '',disabled: true}),
      documentNumberDT : new FormControl({value: '',disabled: true}),
      cityDT : new FormControl({value: '',disabled: true}),
      addressNameD : new FormControl({value: '',disabled: true}),
      cityD : new FormControl({value: '',disabled: true}),
      stateD: new FormControl({value: '',disabled: true}),
      countryD: new FormControl({value: '',disabled: true}),
/*       contactType1: new FormControl({value: '',disabled: true}),
      contactType2: new FormControl({value: '',disabled: true}),
      contactType3: new FormControl({value: '',disabled: true}),
      contact: new FormControl({value: '',disabled: true}),
      contact2: new FormControl({value: '',disabled: true}),
      contact3: new FormControl({value: '',disabled: true}),
      isPreferential1: new FormControl({value: '',disabled: true}),
      isPreferential2: new FormControl({value: '',disabled: true}),
      isPreferential3: new FormControl({value: '',disabled: true}),
      isChecked1: new FormControl({value: '',disabled: true}),
      isChecked2: new FormControl({value: '',disabled: true}),
      isChecked3: new FormControl({value: '',disabled: true}), */
      membershipDate: new FormControl({value: '',disabled: true}),
      gender: new FormControl({value: '',disabled: true}),
      branch: new FormControl({value: '',disabled: true}),
      managerId: new FormControl({value: '',disabled: true}),
      customerType: new FormControl({value: '',disabled: true}),
      education: new FormControl({value: '',disabled: true}),
      profession: new FormControl({value: '',disabled: true}),
      cardname: new FormControl({value: '',disabled: true}),
      namePI: new FormControl({value: '',disabled: true}),
      documentTypePI: new FormControl({value: '',disabled: true}),
      documentNumberPI: new FormControl({value: '',disabled: true}),
      personsInCharge: new FormControl({value: '',disabled: true})

    });

    this.fgConsultFatca = this.fbConsultFatca.group({
      idFatca: new FormControl({value: '',disabled: true}),
      oblifis: new FormControl({value: '',disabled: true}),
      cantobl: new FormControl({value: '',disabled: true}),
      descriptionCRS: new FormControl({value: '',disabled: true}),
      dateValueC: new FormControl({value: '',disabled: true}),
      dateValueM: new FormControl({value: '',disabled: true}),
    });

    this.fgConsultEconomic = this.fbConsultEconomic.group({
      economicActivity : new FormControl({value: '',disabled: true}),
      occupationType : new FormControl({value: '',disabled: true}),
      incomesType : new FormControl({value: '',disabled: true}),
      currency : new FormControl({value: '',disabled: true}),
      amount : new FormControl({value: '',disabled: true}),
      profesionPosition : new FormControl({value: '',disabled: true}),
      workStartDate : new FormControl({value: '',disabled: true}),
      employmentSituation : new FormControl({value: '',disabled: true}),
      activity : new FormControl({value: '',disabled: true}),
    })

    this.fgConsultAddress = this.fbConsultAddress.group({
      addressType : new FormControl({value: '',disabled: true}),
      country : new FormControl({value: '',disabled: true}),
      state : new FormControl({value: '',disabled: true}),
      city : new FormControl({value: '',disabled: true}),
      addressName : new FormControl({value: '',disabled: true}),
      zipCode : new FormControl({value: '',disabled: true}),
      stratum : new FormControl({value: '',disabled: true}),
      antiquity : new FormControl({value: '',disabled: true}),
      additionalInformation : new FormControl({value: '',disabled: true}),
    })

    this.fgConsultContact = this.fbConsultContact.group({
      contact : new FormControl({value: '',disabled: true}),
      contactType : new FormControl({value: '',disabled: true}),
      country : new FormControl({value: '',disabled: true}),
      isPreferential : new FormControl({value: '',disabled: true}),
      isChecked : new FormControl({value: '',disabled: true}),
    })

  }

  ngOnInit(): void {
    this.getCustomers()

  }

  closeConsult() {
    this.ServiceCustomer.$modalConsult.emit(false);
  }


  async getCustomers() {
    console.log('ask someone if getCustomers work');
    this.ServiceCustomer.getCustomer().subscribe(async dataConsultById => {
      let lasname;
      if (dataConsultById['surnames']) {
        lasname = dataConsultById['surnames'];
      } else {
        lasname = '';
      }

      let Tam = dataConsultById['contactDetails'].length;
      let cont = 0;
      while (cont <= Tam - 1) {
        let tc = dataConsultById['contactDetails'][cont]['contactType']['name'];
        let tempTc = this.ArrTypeContac.push(tc);
        let nc = dataConsultById['contactDetails'][cont]['contact'];
        let tempNc = this.ArrContac.push(nc);
        let pc = dataConsultById['contactDetails'][cont]['isPreferential'];
        let tempPc = this.ArrPrefeContac.push(pc);
        let vc = dataConsultById['contactDetails'][cont]['isChecked'];
        let tempVc = this.ArrCheckContac.push(vc);
        cont++;
      }

      /******* */
/*       this.fgCustomer = this.fbCustomer.group({
        clientId: new FormControl({ value: dataConsultById['clientId'], disabled: true }),
        firstName: new FormControl({ value: dataConsultById['firstName'], disabled: true }),
        lastName: new FormControl({ value: dataConsultById['lastName'], disabled: true }),
        surnames: new FormControl({ value: lasname, disabled: true }),
        birthDate: new FormControl({ value: dataConsultById['birthDate'], disabled: true }),
        cityBir: new FormControl({ value: dataConsultById['identityDocuments'][0]['city'], disabled: true }),
        nationalities: new FormControl({ value: dataConsultById['nationalities'][0]['id'], disabled: true }),
        documentTypeDT: new FormControl({ value: dataConsultById['identityDocuments'][0]['documentType']['name'], disabled: true }),
        documentNumberDT: new FormControl({ value: dataConsultById['identityDocuments'][0]['documentNumber'], disabled: true }),
        cityDT: new FormControl({ value: dataConsultById['identityDocuments'][0]['city'], disabled: true }),
        addressNameD: new FormControl({ value: dataConsultById['legalAddress']['addressName'], disabled: true }),
        cityD: new FormControl({ value: dataConsultById['legalAddress']['city'], disabled: true }),
        stateD: new FormControl({ value: dataConsultById['legalAddress']['state'], disabled: true }),
        countryD: new FormControl({ value: dataConsultById['legalAddress']['country']['name'], disabled: true }),
        membershipDate: new FormControl({ value: dataConsultById['membershipDate'], disabled: true }),
        gender: new FormControl({ value: dataConsultById['gender']['id'], disabled: true }),
        branch: new FormControl({ value: dataConsultById['bank']['branch']['name'], disabled: true }),
        managerId: new FormControl({ value: dataConsultById['manager']['managerId'], disabled: true }),
        customerType: new FormControl({ value: dataConsultById['customerType']['name'], disabled: true }),
        education: new FormControl({ value: dataConsultById['education']['level'], disabled: true }),
        profession: new FormControl({ value: dataConsultById['profession'], disabled: true }),
        cardname: new FormControl({ value: dataConsultById['cardName'], disabled: true }),
        namePI: new FormControl({ value: dataConsultById['partnerInformation']['name'], disabled: true }),
        documentTypePI: new FormControl({ value: dataConsultById['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name'], disabled: true }),
        documentNumberPI: new FormControl({ value: dataConsultById['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber'], disabled: true }),
        personsInCharge: new FormControl({ value: dataConsultById['personsInCharge'], disabled: true })

      }); */
      /****** */

      console.log(dataConsultById);
      this.fgCustomer.setValue({
        clientId : dataConsultById['clientId'],
        firstName : dataConsultById['firstName'],
        lastName : dataConsultById['lastName'],
        surnames:  lasname,
        birthDate: dataConsultById['birthDate'],
        cityBir: dataConsultById['identityDocuments'][0]['city'],
        nationalities: dataConsultById['nationalities'][0]['id'],
        documentTypeDT: dataConsultById['identityDocuments'][0]['documentType']['name'],
        documentNumberDT: dataConsultById['identityDocuments'][0]['documentNumber'],
        cityDT: dataConsultById['identityDocuments'][0]['city'],
        addressNameD: dataConsultById['legalAddress']['addressName'],
        cityD: dataConsultById['legalAddress']['city'],
        stateD: dataConsultById['legalAddress']['state'],
        countryD: dataConsultById['legalAddress']['country']['name'],
        membershipDate:dataConsultById['membershipDate'],
        gender: dataConsultById['gender']['id'],
        branch: dataConsultById['bank']['branch']['name'],
        managerId: dataConsultById['manager']['managerId'],
        customerType: dataConsultById['customerType']['name'],
        education: dataConsultById['education']['level'],
        profession: dataConsultById['profession'],
        cardname: dataConsultById['cardName'],
        namePI: dataConsultById['partnerInformation']['name'],
        documentTypePI: dataConsultById['partnerInformation']['partnerIdentityDocuments'][0]['documentType']['name'],
        documentNumberPI: dataConsultById['partnerInformation']['partnerIdentityDocuments'][0]['documentNumber'],
        personsInCharge: dataConsultById['personsInCharge']       
      }) 

    });

    this.ServiceCustomer.getFatca().subscribe(dataConsultFatca => {
      console.log(dataConsultFatca['obligation']);
      let valorIndicador;

      let Tam = dataConsultFatca['indicators'].length;
      let cont = 0;
      while (cont <= Tam - 1) {
        let iId = dataConsultFatca['indicators'][cont]['indicatorId'];
        let tempIi = this.ArrIdIndic.push(iId);
        let iNa = dataConsultFatca['indicators'][cont]['name'];
        let tempNa = this.ArrNamIndic.push(iNa);
        let iVal = dataConsultFatca['indicators'][cont]['value'];
        if(iVal){
          valorIndicador = dataConsultFatca['indicators'][cont]['value'];
        }else{
          valorIndicador = '';
        }
        let tempIval = this.ArrValIndic.push(valorIndicador);
        cont++;
      }

      let tamObli = dataConsultFatca['obligation'].length;
      let contObli = 0;
      while (contObli <= tamObli - 1) {
        let oCc = dataConsultFatca['obligation'][contObli]['countryCode'];
        let tempOcc = this.ArrCodObli.push(oCc);
        let oId = dataConsultFatca['obligation'][contObli]['idObligation'];
        let tempOid = this.ArrIdObli.push(oId);
        contObli++;
      }



      this.fgConsultFatca.setValue({
        idFatca: dataConsultFatca['idFatca'],
/*         countryCode1: dataConsultFatca['obligation'][0]['countryCode'],
        idObligation1: dataConsultFatca['obligation'][0]['idObligation'],
        countryCode2: dataConsultFatca['obligation'][1]['countryCode'],
        idObligation2: dataConsultFatca['obligation'][1]['idObligation'],
        countryCode3: dataConsultFatca['obligation'][2]['countryCode'],
        idObligation3: dataConsultFatca['obligation'][2]['idObligation'],
        countryCode4: dataConsultFatca['obligation'][3]['countryCode'],
        idObligation4: dataConsultFatca['obligation'][3]['idObligation'], */
        oblifis: dataConsultFatca['oblifis'],
        cantobl: dataConsultFatca['cantobl'],
        descriptionCRS: dataConsultFatca['descriptionCRS'],
        dateValueC: dataConsultFatca['dateFatca'][0]['dateValue'],
        dateValueM: dataConsultFatca['dateFatca'][1]['dateValue'],
      })
    });

    this.ServiceCustomer.getEconomic().subscribe(dataListEconomic => {
      console.log(dataListEconomic);

/*       if(dataListEconomic['incomes']){
        console.log(dataListEconomic['incomes'].length);
      } */

      this.fgConsultEconomic.setValue({
        economicActivity : dataListEconomic['economicActivity']['name'],
        occupationType : dataListEconomic['occupationType']['name'],
        incomesType : dataListEconomic['incomes'][0]['incomesType']['id'],
        currency : dataListEconomic['incomes'][0]['incomesValue']['currency'],
        amount : dataListEconomic['incomes'][0]['incomesValue']['amount'],
        profesionPosition : dataListEconomic['workPlace']['profesionPosition'],
        workStartDate : dataListEconomic['workPlace']['workStartDate'],
        employmentSituation : dataListEconomic['employmentSituation'],
        activity : dataListEconomic['workPlace']['activity'],
      })
      
    });

    this.ServiceCustomer.getAddress().subscribe(dataListAddress => { 
      console.log(dataListAddress);
      this.infoAdicional;
      if(dataListAddress[0]['location']['additionalInformation']){
        this.infoAdicional = dataListAddress[0]['location']['additionalInformation']
      }else{
        this.infoAdicional = '';
      }

      this.fgConsultAddress.setValue({
        addressType : dataListAddress[0]['addressType']['name'],
        country : dataListAddress[0]['location']['country']['name'],
        state : dataListAddress[0]['location']['state']['name'],
        city : dataListAddress[0]['location']['city'],
        addressName : dataListAddress[0]['location']['addressName'],
        zipCode : dataListAddress[0]['location']['zipCode'],
        stratum : dataListAddress[0]['location']['stratum'],
        antiquity : dataListAddress[0]['antiquity'],
        additionalInformation : this.infoAdicional,
      })
    });

    this.ServiceCustomer.getContact().subscribe(dataListContact => {
      console.log(dataListContact);

      this.fgConsultContact.setValue({
        contact : dataListContact[0]['contact'],
        contactType : dataListContact[0]['contactType']['name'],
        country : dataListContact[0]['country']['name'],
        isPreferential : dataListContact[0]['isPreferential'],
        isChecked : dataListContact[0]['isChecked'],

      })
    });


  }

  UpdateClient() {
    this.disableInputbox = !this.disableInputbox;
  }

}
<div id="global" class="modal">
    <div class="container-modal">
        <body class="body-consult">
            <form [formGroup]="fgCustomer">
                <div class="button-close">
                    <input type="image" (click)="closeConsult()" src="https://cdn-icons-png.flaticon.com/512/458/458595.png">
                    <div class="spacer"></div>
                </div>
                <div class="user-details">
                    <div class="subtitle">
                        <h2>DATOS BÁSICOS</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Cliente: </span>
                        <input type="text" maxlength="16" formControlName="customerType">
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación Interna: </span>
                        <input type="text" maxlength="16" formControlName="clientId">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="16" formControlName="documentTypeDT">
                    </div>
                    <div class="input-box">
                        <span class="details">Número Identificación: </span>
                        <input type="text"  maxlength="15" formControlName="documentNumberDT">
                    </div>
                    <div class="input-box">
                        <span class="details">Nombres: </span>
                        <input type="text" maxlength="20" formControlName="firstName">
                    </div>
                    <div class="input-box">
                        <span class="details">Primer Apellido: </span>
                        <input type="text" maxlength="20" formControlName="lastName">
                    </div>
                    <div class="input-box">
                        <span class="details">Segundo Apellido: </span>
                        <input type="text" maxlength="20" formControlName="surnames">
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Género: </span>
                            <div class="category">
                                <label for="Id_genderF">F</label>
                                <input id="Id_genderF" type="radio" maxlength="1" value="F" formControlName="gender">
                                <label for="Id_genderM">M</label>
                                <input id="Id_genderM" type="radio" maxlength="1" value="M" formControlName="gender">
                            </div>
                    </div>  
                    <div class="input-box">
                        <span class="details">Fecha Nacimiento: </span>
                        <input type="text" maxlength="10" formControlName="birthDate">
                    </div>   
                    <div class="input-box">
                        <span class="details">Ciudad Expedición: </span>
                        <input type="text" maxlength="4" formControlName="cityBir">
                    </div>                                                      
                    <div class="input-box">
                        <span class="details">Nacionalidad: </span>
                        <input type="text" maxlength="4" formControlName="nationalities">
                    </div>  
                    <div class="input-box">
                        <span class="details">Personas a Cargo: </span>
                        <input type="number" maxlength="2" formControlName="personsInCharge">
                    </div>    
                    <div class="input-box">
                        <span class="details">Profesión: </span>
                        <input type="text" maxlength="2" formControlName="profession">
                    </div> 
                    <div class="input-box">
                        <span class="details">Nivel Educativo: </span>
                        <input type="text" maxlength="2" formControlName="education">
                    </div>   
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE RESIDENCIA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">País Residencia: </span>
                        <input type="text" maxlength="3" formControlName="countryD">
                    </div>
                    <div class="input-box">
                        <span class="details">Departamento Residencia: </span>
                        <input type="text" maxlength="4" formControlName="stateD">
                    </div>  
                    <div class="input-box">
                        <span class="details">Ciudad Residencia: </span>
                        <input type="text" maxlength="20" formControlName="cityD">
                    </div> 
                    <div class="input-box">
                        <span class="details">Dirección Residencia: </span>
                        <input type="text" maxlength="50" formControlName="addressNameD">
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DE CONTACTO</h2>
                    </div>
                    <div class="contact-details">
                        <div class="input-box">
                            <span>Tipo</span>
                            <input type="text" *ngFor="let TypeC of ArrTypeContac; let i = index" id="contactType{{i+1}}" [ngModel]="TypeC" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" *ngFor="let TypeC of ArrTypeContac; let i = index" formControlName="contactType{{i+1}}" [ngModel]="TypeC" [disabled]="true">
                            <input type="text" maxlength="15" formControlName="contactType2">
                            <input type="text" maxlength="15" formControlName="contactType3"> -->
                        </div>
                        <div class="input-box">
                            <span>Número</span>
                            <input type="text" *ngFor="let NumC of ArrContac" [ngModel]="NumC" [ngModelOptions]="{standalone: true}" [disabled]="true">
<!--                             <input type="text" maxlength="80" formControlName="contact">
                            <input type="text" maxlength="80" formControlName="contact2">
                            <input type="text" maxlength="80" formControlName="contact3"> -->
                        </div>
                        <div class="input-box">
                            <span>Preferencial</span>
                            <input type="text" *ngFor="let PrefC of ArrPrefeContac" [ngModel]="PrefC" [ngModelOptions]="{standalone: true}" [disabled]="true">
<!--                             <input type="text" maxlength="5" formControlName="isPreferential1">
                            <input type="text" maxlength="5" formControlName="isPreferential2">
                            <input type="text" maxlength="5" formControlName="isPreferential3"> -->
                        </div>
                        <div class="input-box">
                            <span>Verificado</span>
                            <input type="text" *ngFor="let VerC of ArrCheckContac" [ngModel]="VerC" [ngModelOptions]="{standalone: true}" [disabled]="true">
<!--                             <input type="text" maxlength="5" formControlName="isChecked1">
                            <input type="text" maxlength="5" formControlName="isChecked2">
                            <input type="text" maxlength="5" formControlName="isChecked3"> -->
                        </div>
                    </div>  
                    <div class="subtitle">
                        <h2>INFORMACIÓN DEL SOCIO</h2>
                    </div>                           
                    <div class="input-box">
                        <span class="details">Sucursal: </span>
                        <input type="text" maxlength="4" formControlName="branch">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Identificación: </span>
                        <input type="text" maxlength="20" formControlName="documentTypePI">
                    </div>
                    <div class="input-box">
                        <span class="details">Identificación: </span>
                        <input type="text" maxlength="15" formControlName="documentNumberPI">
                    </div>
                    <div class="input-box">
                        <span class="details">Nombre: </span>
                        <input type="text" maxlength="20" formControlName="namePI">
                    </div>
                    <div class="input-box">
                        <span class="details">Membresía: </span>
                        <input type="text" maxlength="10" formControlName="membershipDate">
                    </div>
                    <div class="input-box">
                        <span class="details">Tarjeta: </span>
                        <input type="text" maxlength="26" formControlName="cardname">
                    </div>                   
                </div>
<!--                 <div class="button">
                    <input type="submit" value="Actualizar" (click)="UpdateClient()">
                </div> -->
            </form>
            <form [formGroup]="fgConsultFatca">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>CONSULTA FATCA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Id Fatca: </span>
                        <input type="text" maxlength="16" formControlName="idFatca">
                    </div>
                    <div class="gender-details">
                        <span class="gender-title">Obligaciones Fiscales: </span>
                        <!-- <fieldset> -->
                        <div class="category">
                            <label for="Id_oblifisS">Si</label>
                            <input id="Id_oblifisS" type="radio" maxlength="1" value="S" formControlName="oblifis">
                            <label for="Id_oblifisN">No</label>
                            <input id="Id_oblifisN" type="radio" maxlength="1" value="N" formControlName="oblifis">
                        </div>
                        <!-- </fieldset> -->
                    </div>
                    <div class="input-box">
                        <span class="details">Cantidad Obligaciones: </span>
                        <input type="text" maxlength="20" formControlName="cantobl">
                    </div>
                    <div class="input-box">
                        <span class="details">Descripción CRS: </span>
                        <input type="text" maxlength="20" formControlName="descriptionCRS">
                    </div>
                    <div class="subtitle">
                        <h3>Indicadores</h3>
                    </div>
                    <div class="economic-details">
                        <div class="input-box">
                            <span>Id Indicador</span>
                            <input type="text" *ngFor="let IndicadorId of ArrIdIndic; let i = index" id="indicatorId{{i+1}}" [ngModel]="IndicadorId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="21" formControlName="indicatorIdI">
                            <input type="text" maxlength="21" formControlName="indicatorIdI2">
                            <input type="text" maxlength="21" formControlName="indicatorIdI3">
                            <input type="text" maxlength="21" formControlName="indicatorIdI4"> -->
                        </div>
                        <div class="input-box">
                            <span>Nombre</span>
                            <input type="text" *ngFor="let IndicadorName of ArrNamIndic; let i = index" id="name{{i+1}}" [ngModel]="IndicadorName" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="nameI">
                            <input type="text" maxlength="3" formControlName="nameI2">
                            <input type="text" maxlength="3" formControlName="nameI3">
                            <input type="text" maxlength="3" formControlName="nameI4"> -->
                        </div>
                        <div class="input-box">
                            <span>Valor</span>
                            <input type="text" *ngFor="let IndicadorValue of ArrValIndic; let i = index" id="value{{i+1}}" [ngModel]="IndicadorValue" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="valueI">
                            <input type="text" maxlength="3" formControlName="valueI2">
                            <input type="text" maxlength="3" formControlName="valueI3">
                            <input type="text" maxlength="3" formControlName="valueI4"> -->
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Obligaciones</h3>
                        <p *ngIf="ArrIdObli.length == 0"> No hay Obligaciones para mostrar</p>
                    </div>
                    <div class="economic-details" *ngIf="ArrIdObli.length != 0">
                        <div class="input-box">
                            <span>Código Obligación</span>
                            <input type="text" *ngFor="let ObligacionCode of ArrCodObli; let i = index" id="countryCode{{i+1}}" [ngModel]="ObligacionCode" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="21" formControlName="countryCode1">
                            <input type="text" maxlength="21" formControlName="countryCode2">
                            <input type="text" maxlength="21" formControlName="countryCode3">
                            <input type="text" maxlength="21" formControlName="countryCode4"> -->
                        </div>
                        <div class="input-box">
                            <span>Id Obligación</span>
                            <input type="text" *ngFor="let ObligacionId of ArrIdObli; let i = index" id="idObligation{{i+1}}" [ngModel]="ObligacionId" [ngModelOptions]="{standalone: true}" [disabled]="true">
                            <!-- <input type="text" maxlength="3" formControlName="idObligation1">
                            <input type="text" maxlength="3" formControlName="idObligation2">
                            <input type="text" maxlength="3" formControlName="idObligation3">
                            <input type="text" maxlength="3" formControlName="idObligation4"> -->
                        </div>
                    </div>
                    <div class="data-date">
                        <div class="input-box">
                            <span class="details">Fecha Creación: </span>
                            <!-- <p class="spanData">ffgfgfgfg</p> -->
                            <input type="text" formControlName="dateValueC">
                        </div>
                        <div class="input-box">
                            <span class="details">Fecha Modificación: </span>
                            <!-- <p class="spanData">{{fecha}}</p> -->
                            <input type="text" formControlName="dateValueM">
                        </div>
                    </div>
                </div>
            </form>
            <form [formGroup]="fgConsultEconomic">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN ECONÓMICA</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Actividad Económica: </span>
                        <input type="text" maxlength="40" formControlName="economicActivity">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Ocupación: </span>
                        <input type="text" maxlength="79" formControlName="occupationType">
                    </div>
                    <div class="economic-details">
                        <div class="input-box">
                            <span>Tipo Ingreso</span>
                            <input type="text" maxlength="21" formControlName="incomesType">
                        </div>
                        <div class="input-box">
                            <span>Moneda</span>
                            <input type="text" maxlength="3" formControlName="currency">
                        </div>
                        <div class="input-box">
                            <span>Monto</span>
                            <input type="text" maxlength="21" formControlName="amount">
                        </div>
                    </div>
                    <div class="subtitle">
                        <h3>Información Laboral</h3>
                    </div>
                    <div class="input-box">
                      <span class="details">Puesto/Cargo: </span>
                      <input type="text" maxlength="18" formControlName="profesionPosition">
                    </div>
                    <div class="input-box">
                      <span class="details">Fecha Inicio </span>
                      <input type="text" maxlength="10" formControlName="workStartDate">
                    </div>
                    <div class="input-box">
                      <span class="details">Situación Laboral: </span>
                      <input type="text" maxlength="17" formControlName="employmentSituation">
                    </div>
                    <div class="input-box">
                      <span class="details">Actividad: </span>
                      <input type="text" formControlName="activity">
                    </div>
                </div>
            </form>
            <form [formGroup]="fgConsultAddress">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN DIRECCIONES</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Propiedad: </span>
                        <input type="text" maxlength="40" formControlName="addressType">
                    </div>
                    <div class="input-box">
                        <span class="details">País: </span>
                        <input type="text" maxlength="79" formControlName="country">
                    </div>
                    <div class="input-box">
                      <span class="details">Departamento: </span>
                      <input type="text" maxlength="18" formControlName="state">
                    </div>
                    <div class="input-box">
                      <span class="details">Ciudad: </span>
                      <input type="text" maxlength="10" formControlName="city">
                    </div>
                    <div class="input-box">
                      <span class="details">Dirección: </span>
                      <input type="text" maxlength="17" formControlName="addressName">
                    </div>
                    <div class="input-box">
                      <span class="details">Código Postal: </span>
                      <input type="text" formControlName="zipCode">
                    </div>
                    <div class="input-box">
                      <span class="details">Estrato </span>
                      <input type="text" formControlName="stratum">
                    </div>
                    <div class="input-box">
                      <span class="details">Antiguedad: </span>
                      <input type="text" formControlName="antiquity">
                    </div>
                    <div class="input-box" *ngIf="infoAdicional != ''">
                      <span class="details">Información Adicional</span>
                      <input type="text" formControlName="additionalInformation">
                    </div>
                </div>
            </form>
            <form [formGroup]="fgConsultContact">
                <div class="user-details">
                    <div class="subtitle">
                        <h2>INFORMACIÓN CONTACTOS</h2>
                    </div>
                    <div class="input-box">
                        <span class="details">Contacto: </span>
                        <input type="text" maxlength="80" formControlName="contact">
                    </div>
                    <div class="input-box">
                        <span class="details">Tipo Contacto: </span>
                        <input type="text" maxlength="20" formControlName="contactType">
                    </div>
                    <div class="input-box">
                      <span class="details">País: </span>
                      <input type="text" maxlength="20" formControlName="country">
                    </div>
                    <div class="input-box">
                      <span class="details">Preferencial: </span>
                      <input type="text" formControlName="isPreferential">
                    </div>
                    <div class="input-box">
                      <span class="details">Verificado: </span>
                      <input type="text" formControlName="isChecked">
                    </div>
                </div>
            </form>
            <!-- <app-consult-fatca></app-consult-fatca> --> 
            <!-- <app-list-economic></app-list-economic> -->
            <!-- <app-list-address></app-list-address> -->
            <!-- <app-list-contact></app-list-contact> -->
        </body>   
    </div>
</div>
